import React, { useEffect, useState } from 'react';
import './Search.css';
import { Button, Typography } from '@mui/material';
import { allUsers } from '../../Actions/Users';
import { useDispatch, useSelector } from 'react-redux';
import User from '../User/User';

const Search=()=>{
    const [name,setName]=useState('');
    const dispatch=useDispatch();
    const {users,loading}=useSelector(state=>state.allUsers);

    const submitHandler=(e)=>{
          e.preventDefault();    
          dispatch(allUsers(name));
    }

    useEffect(()=>{
        dispatch(allUsers(name))
    },[])

     return <>
            <div className='update'>
            <form className="updateForm" onSubmit={submitHandler}>
                <input type="text"placeholder='Search here' value={name} onChange={(e)=>setName(e.target.value)} required={true}/>
                <Button disabled={loading} type="submit">Search</Button>   
                <div className='allUsersStyle'>jhgjh
                     {users && users.map((user)=>{
                         <User key={user._id} name={user.name} avatar={user.avatar.url} userId={user._id}/>
                     })}
            </div>             
            </form>
           
        </div>
      </>
}

export default Search;
