import React, { useState } from 'react';
import './Search.css';
import { Button, Typography } from '@mui/material';
import { allUsers } from '../../Actions/Users';
import { useDispatch, useSelector } from 'react-redux';

const Search=()=>{
    const [name,setName]=useState('');
    const dispatch=useDispatch();
    const {users,loading}=useSelector(state=>state.allUsers);

    const submitHandler=(e)=>{
          e.preventDefault();    
          dispatch(allUsers(name));
    }

     return <>
            <div className='update'>
            <form className="updateForm" onSubmit={submitHandler}>
                <input type="text"placeholder='Search here' value={name} onChange={(e)=>setName(e.target.value)} required={true}/>
                <Button disabled={loading} type="submit">Search</Button>
            </form>
        </div>
      </>
}

export default Search;
