import { Avatar, Button, Dialog, Typography } from '@mui/material';
import React, { useEffect, useState } from 'react'
import { useAlert } from 'react-alert';
import { useDispatch, useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import {
  getUserProfile,
} from '../../Actions/Users'
import Loader from '../Loader/Loader';
import Post from '../Post/Post';
import User from '../User/User';

const UserProfile = () => {
  const dispatch=useDispatch();
  const alert=useAlert();

  const {user,loading:userLoading,error:userError}=useSelector(state=>state.UserProfile);
  const {user:me,loading:userloading}=useSelector(state=>state.user);
  const {loading,error,posts}=useSelector(state=>state.myPost);
  const {error:likeError,message,loading: deleteLoading,}=useSelector(state=>state.like);
  const [followersToggle,setFollowersToggle]=useState(false);
  const [followingToggle,setFollowingToggle]=useState(false);
  const [following,setFollowing]=useState(false);
  const [isProfile,setIsProfile]=useState(false);
  
  const params=useParams();

  useEffect(() => {
    dispatch(getUserProfile(params.id));
  }, [dispatch, params.id]);

  useEffect(() => {
      if(me._id===params.id){
           setIsProfile(true)
      }
      dispatch(getUserProfile(params.id))
  }, [dispatch,me._id,params.id])
    

    useEffect(() => {
         if(error){
          alert.error(error);
          dispatch({
             type:"clearErrors"
          })
         }
         if(likeError){
            alert.error(likeError)
            dispatch({
                type:'clearErrors'
            })
         }
         if(message){
            alert.success(message)
            dispatch({
                type:'clearMessage'
            })
         }
    
    }, [alert,error,likeError,message,dispatch])
  
  return (
      loading===true || userloading===true?<Loader/>:(
        <div className='account'>
              <div className='accountLeft'>
                     {
                    posts && posts.length>0?posts.map((post)=>(
                         <Post  
                                key={post._id}
                                postId={post._id}
                                caption={post.caption}
                                postImage={post.imageUrl.url}
                                likes = {post.likes}
                                comments = {post.comments}
                                ownerImage={post.owner.avatar.url}
                                ownerName={post.owner.name}
                                ownerId={post.owner._id}
                                isDelete = {true}
                                isAccount = {true}
                         />
                    )):<Typography variant='h6' margin='2vmax'>No posts available</Typography>
                  }
              </div>
              <div className='accountright'>
                   <Avatar src={user.avatar.url}  sx={{height:"7vmax",width:"7vmax"}} marginTop={"2vmax"}/>
                   <Typography variant="h6" marginTop={"1vmax"} color={'#3B3B3B'} marginBottom={"2vmax"}>{user.name}</Typography>
                   <div className='a'>
                      <button onClick={()=>setFollowingToggle(!followingToggle)}>Following {me.following.length}</button>
                   </div>
                    <div className='a'>
                      <button onClick={()=>setFollowersToggle(!followersToggle)}>Followers {me.followers.length}</button>
                   </div>
                    <div className='a'>
                       Posts {user.posts.length}
                   </div>
                  {
                    isProfile?null: <Button variant="contained" onClick={()=>setFollowing(!following)} style={{backgroundColor:following?"red":""}}>{
                       following?"UnFollow":"Follow"
                   }</Button>
                  }

                    {/* DialogBox for followers */}
                    <Dialog open={followersToggle} onClose={() => { setFollowersToggle(!followersToggle) }}>
                        <div className='dialogBox'>
                           <Typography variant='h6'>Followers</Typography>
                    {
                         me && me.followers.length>0?me.followers.map((follower)=>(
                              <User key={follower._id} name={follower.name} avatar={follower.avatar.url} userId={follower._id} />
                         )):<Typography style={{margin:'2vmax'}}>No Followers present</Typography>
                    }
                </div>
            </Dialog>

             {/* DialogBox for following */}
                    <Dialog open={followingToggle} onClose={() => { setFollowingToggle(!followingToggle) }}>
                        <div className='dialogBox'>
                           <Typography variant='h6'>Following</Typography>
                    {
                        me && me.following.length>0?me.following.map((follow)=>(
                              <User key={follow._id} name={follow.name} avatar={follow.avatar.url} userId={follow._id} />
                         )):<Typography style={{margin:'2vmax'}}>User has not made any posts</Typography>
                    }
                </div>
            </Dialog>
              </div>
        </div>
      )
  )
}

export default UserProfile
